function toggleNav(){const e=document.querySelector(".js-open-main-nav"),o=document.querySelector(".js-close-main-nav");e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(".main-nav").classList.add("open"),document.body.classList.add("noscroll")}),o.addEventListener("click",function(e){e.preventDefault(),document.querySelector(".main-nav").classList.remove("open"),document.body.classList.remove("noscroll")})}function toggleAccordion(){const e=document.querySelectorAll(".js-accordion-toggle");document.body.contains(document.querySelector(".js-accordion-toggle"))&&e.forEach(function(e){e.addEventListener("click",function(){this.parentNode.classList.toggle("show")})})}function initCarousel(){if(document.body.contains(document.querySelector(".carousel-1"))){var e=document.querySelectorAll(".carousel-1");const o={type:"carousel",focusAt:"center",startAt:0,perView:1};Object.values(e).map(e=>{new Glide(e,o).mount()})}}"loading"!==document.readyState?(toggleNav(),toggleAccordion(),initCarousel()):document.addEventListener("DOMContentLoaded",function(){toggleNav(),toggleAccordion(),initCarousel()});