function toggleNav(){const e=document.querySelector(".js-open-nav"),o=document.querySelector(".js-close-nav");e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(".header__nav").classList.add("open"),document.body.classList.add("noscroll")}),o.addEventListener("click",function(e){e.preventDefault(),document.querySelector(".header__nav").classList.remove("open"),document.body.classList.remove("noscroll")})}function toggleAccordion(){const e=document.querySelectorAll(".js-accordion-toggle");document.body.contains(document.querySelector(".js-accordion-toggle"))&&e.forEach(function(e){e.addEventListener("click",function(){this.parentNode.classList.toggle("show")})})}"loading"!==document.readyState?(toggleNav(),toggleAccordion()):document.addEventListener("DOMContentLoaded",function(){toggleNav(),toggleAccordion()});